select.if:
SELECT Registration.registrationId, Registration.competitionId, Registration.applicantId, Registration.registrationTime, Registration.state, Registration.auditeason, `User`.userName as applicantName FROM Registration , `User`
applicantId=userId
and registrationId=#{registrationId}
and competitionId=#{competitionId}
and applicantId=#{applicantId}
and Registration.state=#{state}
ORDER BY registrationTime

SELECT Registration.registrationId, Registration.competitionId, Registration.applicantId, Registration.registrationTime, Registration.state, Registration.auditeason, `Team`.teamName as applicantName FROM Registration , `Team`
applicantId=teamId
and competitionId=#{competitionId}
and applicantId=#{applicantId}
and Registration.state=#{state}
and registrationId=#{registrationId}
ORDER BY registrationTime

mapper.select:
SELECT
Count(*)
FROM
Registration ,
`Team`

 competitionId=userId

  and competitionId=#{competitionId}


 and applicantId=#{applicantId}


 and Registration.state=#{state}


	and registrationId=#{registrationId}
